			MORE_flag = 0;
			if (do_signal(child, __TEST_EXDATA_REQ) != __RESULT_SUCCESS)
				goto failure;
			state++;
			goto expect_error;
		case T_INFO_REQ:
			break;
		case T_OPTDATA_REQ:
			test_data = "Option data.";
			DATA_flag = 0;
			test_opts = &opt_data;
			test_olen = sizeof(opt_data);
			if (do_signal(child, __TEST_OPTDATA_REQ) != __RESULT_SUCCESS)
				goto failure;
			state++;
